/*-------------accordion-----------------*/


.accordion {
    cursor: pointer;
    padding: 5%;
    width: 100%;
    border: none;
    text-align: left;
    outline: none;
    transition: 0.2s;
  	font-family:Helvetica;
    font-size:1.4em;
    background: white;
    flex-grow: 1;
  	color:black;
  	border-top: 1px solid lightgrey;
    flex-basis:auto;
} 
.accordion:after {
 	content: "\f078";
    font-size: 13px;
    color: #777;
    float: right;
    margin-left: 5px;
  	font:normal normal normal 14px/1 FontAwesome;
}  
.accordion.active:after {
    content: "\f077";
}




